mapper.select:
SELECT COUNT(*)
FROM evaluates
WHERE userId = #{userId}
AND bookId = #{bookId}

SELECT COUNT(*)
FROM evaluates
WHERE userId = #{userId}
AND bookId = #{bookId}
AND state = 0

SELECT COUNT(*)
FROM evaluates
WHERE userId = #{userId}
AND bookId = #{bookId}
AND state = 1

mapper.update:
UPDATE evaluates
SET state = #{state}
WHERE userId = #{dto.userId}
AND bookId = #{dto.bookId}
